$(document).ready(function () {
  if (window.location.href.indexOf("about") > -1) {
    var element = document.getElementById("azen");
    element.classList.add("active");
    var feed = "<span class='text-warning'>About</span>"
    document.getElementById('feedback').innerHTML = feed;
      var flyer = document.getElementById('response');
      flyer.innerHTML = ("<h1 class='text-uppercase fs-lg-7 fs-5 fw-bolder text-300 lh-1 position-relative z-index-0'>About</h1> <h1 class='d-none d-md-block fw-bolder text-outlined fs-7 text-white lh-1 mt-n4 position-relative z-index--1'>Our Team</h1>")
  }
  else{
    var element = document.getElementById("hzen");
    element.classList.remove("active");
    document.getElementById('response').style.display = "none";
  }
});
